import React, { useEffect } from 'react'
import { FlightData } from '../../models/FlightData';
import ShowFlight from './ShowFlight';
import { getAllFlights } from '../../services/FlightResponse.service';

const Flight: React.FC = () => {
    const [flights, setFlights] = React.useState<FlightData[]>([]);
    const [loading, setLoading] = React.useState<boolean>(true);
    const [error, setError] = React.useState<string>("");
    const [isInfo, setIsInfo] = React.useState<string | null>(null);

    const fetchFlights = async () => {
        try{
            const flightsData = await getAllFlights();
            setFlights(flightsData);
        }catch(error){
            setError("Error fetching data");
        }finally{
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchFlights();
    });

    if (loading) {
        return <h1>Đang tải...</h1>
    }
    if (error) {
        return <h1>{error}</h1>
    }

    return (
        <div>
            {
                flights.map((flight: FlightData, index: number) => (
                    <ShowFlight key={index} flight={flight} />
                ))
            }
        </div>
    )
}

export default Flight
