import React from 'react'
import { IoAirplaneSharp } from 'react-icons/io5'
import { FlightData } from '../../models/FlightData'
import { parseISO, differenceInMinutes, formatDistance } from 'date-fns';


type FlightProps = {
    flight: FlightData
}

const getDayOfWeek = (dateString: string): string => {
    const date = new Date(dateString);
    const daysOfWeek = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
    return daysOfWeek[date.getDay()];
};

const diffBetweenDates = (date1String: string, date2String: string): any => {
    const date1 = parseISO(date1String);
    const date2 = parseISO(date2String);
    const diff = formatDistance(date1, date2);
}

const InfoFlight: React.FC<FlightProps> = ({flight}) => {
    return (
        <div>
            <div className="pt-4 flex justify-between">
                <div>
                    <div className='text-xl'>{flight.departure?.icao}</div>
                    <div className="font-bold text-xl">{flight.departure?.timezone}</div>
                    <div className="text-sm">left 
                        <span className="font-bold">
                            {flight.departure?.gate ? ` ${flight.departure.gate}` : ' none'}
                        </span>
                    </div>
                    <div className="text-blue-500"><a href="#">{flight.departure?.airport} - {flight.departure?.icao}</a></div>
                    <div className="text-sm font-normal"> 
                    {
                        flight.departure?.actual 
                            ? `${getDayOfWeek(flight.departure.actual.slice(0, 10) ?? '')} ${flight.departure.actual.slice(0, 10) ?? ''}` 
                            : `${getDayOfWeek(flight.departure?.estimated?.slice(0, 10) ?? '')} ${flight.departure?.estimated?.slice(0, 10) ?? ''}`
                    }
                    </div>
                    <div>
                        <span className="font-bold">
                            {
                                flight.departure?.actual ? flight.departure.actual.slice(11) : flight.departure?.estimated?.slice(11)
                            }
                        </span> 
                        <span className="text-green-600">
                            {
                                flight.flight_status === 'active' ? ' (on time)' : ''
                            }
                        </span>
                    </div>
                </div>
                <div className="text-right">
                    <div className="text-xl text-right">{flight.arrival?.icao}</div>
                    <div className="font-bold text-xl">{flight.arrival?.timezone}</div>
                    <div className="text-sm">arriving at 
                        <span className="font-bold">
                            {
                                flight.arrival?.gate ? ` ${flight.arrival.gate}` : ' none'
                            }
                        </span>
                    </div>
                    <div className="text-blue-500"><a href="#">{flight.arrival?.airport} - {flight.arrival?.icao}</a></div>
                    <div className="text-sm font-normal">
                        {
                            flight.arrival?.actual 
                                ? `${getDayOfWeek(flight.arrival.actual.slice(0, 10) ?? '')} ${flight.arrival.actual.slice(0, 10) ?? ''}` 
                                : `${getDayOfWeek(flight.arrival?.estimated?.slice(0, 10) ?? '')} ${flight.arrival?.estimated?.slice(0, 10) ?? ''}`
                        }
                    </div>
                    <div>
                        <span className="text-green-600">
                            (33 minutes early)
                            {
                            }
                        </span> 
                        <span className="font-bold">{flight.arrival?.actual ? flight.arrival.actual.slice(11) : flight.arrival?.estimated?.slice(11)}</span>
                    </div>
                </div>
            </div>

            <div className="max-w-6xl mx-auto p-4">
                <div className="relative text-center">
                    <div className="absolute top-1/2 -translate-y-1/2 left-0 w-full h-1 bg-green-300"></div>
                    <div className="absolute top-1/2 -translate-y-1/2 left-0 w-1/2 h-1 bg-green-700"></div>
                    <div className="absolute top-1/2 left-0 transform -translate-y-1/2 w-3 h-3 bg-green-700 rounded-full"></div>
                    <div className="absolute top-1/2 right-0 transform -translate-y-1/2 w-3 h-3 bg-green-600 rounded-full"></div>
                    <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 text-center">
                        <IoAirplaneSharp className='size-6 text-green-700' />
                    </div>
                </div>
            </div>

            <div className="flex justify-between mt-4">
                <div className="text-left">
                    <div className="bg-blue-900 text-white text-center p-1 rounded h-8">6h 27m elapsed</div>
                    <div className='mt-2 text-sm mx-2'>3,280 mi flown</div>
                </div>
                <div className="text-right">
                    <div className="bg-blue-900 text-white text-center p-1 rounded h-8">5h 55m remaining</div>
                    <div className='mt-2 text-sm mx-2'>3,204 mi to go</div>
                </div>
            </div>
        </div>
    )
}

export default InfoFlight
